$checkStmt->bindParam(':userIP', $userIP, PDO::PARAM_STR);
$checkStmt->execute();
$existingUser = $checkStmt->fetch(PDO::FETCH_ASSOC);

if ($existingUser) {
    $updateQuery = "UPDATE users SET sayfa = :pageName WHERE ip = :userIP";
